client_script:
frappe.ready(function() {
    const urlParams = new URLSearchParams(window.location.search);

    let flight = urlParams.get("flight");
    let flight_price = urlParams.get("flight_price");  // <-- match param

    // Set flight automatically
    if (flight) {
        frappe.web_form.set_value("flight", flight);
    }

    // Set price automatically and make it readonly
    if (flight_price) {
        frappe.web_form.set_value("flight_price", flight_price);
        frappe.web_form.set_df_property("flight_price", "read_only", 1);
    }
});

custom_css:
.web-form .btn-primary {
    background-color: #87CEEB !important;   /* Sky Blue */
    border-color: #87CEEB !important;
    color: #fff !important;                /* White Text */
}

/* Hover Effect (Darker Sky Blue) */
.web-form .btn-primary:hover {
    background-color: #00BFFF !important;   /* Deep Sky Blue */
    border-color: #00BFFF !important;
}
